class ToggleFriend{constructor(e){this.toggler=e,this.toggleFriend()}toggleFriend(){$(this.toggler).click((function(e){e.preventDefault();let t=this;$.ajax({type:"POST",url:$(t).attr("href")}).done((function(e){let l;l=1==e.data.deleted?"Add Friend":"Remove Friend",$(t).html(l)})).fail((function(e){console.log("error in completing the request")}))}))}}