class PostComments{constructor(t){this.postId=t,this.postContainer=$("#post-"+t),this.newCommentForm=$(`#post-${t}-comments-form`),this.createComment(t);let e=this;$(" .delete-comment-button",this.postContainer).each((function(){e.deleteComment($(this))}))}createComment(t){let e=this;this.newCommentForm.submit((function(n){n.preventDefault();$.ajax({type:"post",url:"/comment/create",data:$(this).serialize(),success:function(n){let o=e.newCommentDom(n.data.comment);$("#post-comments-"+t).prepend(o),e.deleteComment($(" .delete-comment-button",o)),$(`#post-${t}-comments-form input:text`).val(""),new ToggleLike($(" .toggle-like-button",o)),new Noty({theme:"relax",text:"Comment published!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(t){console.log(t.responseText)}})}))}newCommentDom(t){return $(`<li id="comment-${t._id}">\n                        <p>\n                            \n                            <small>\n                                <a class="delete-comment-button" href="/comment/destroy/${t._id}">X</a>\n                            </small>\n                            \n                            ${t.content}\n                            <br>\n                            <small>\n                                ${t.user.name}\n                            </small>\n                            <small>\n                            \n                                <a class="toggle-like-button" data-likes="0" href="/likes/toggle/?id=${t._id}&type=Comment">\n                                    0 Likes\n                                </a>\n                            \n                            </small>\n\n                        </p>    \n\n                </li>`)}}